/*Table structure for table `ali_user` */

DROP TABLE IF EXISTS `ali_user`;

CREATE TABLE `ali_user` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_name` varchar(10) NOT NULL COMMENT '邮箱名',
  `user_slug` varchar(10) NOT NULL COMMENT '别名',
  `user_nick` varchar(10) NOT NULL COMMENT '昵称',
  `user_psw` char(32) NOT NULL COMMENT '密码md5加密',
  `user_pic` varchar(100) NOT NULL COMMENT '图片地址',
  `user_state` tinyint(4) NOT NULL DEFAULT '1' COMMENT '是否激活 1：已激活 0：没激活',
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `user_name` (`user_name`),
  UNIQUE KEY `user_slug` (`user_slug`),
  UNIQUE KEY `user_nick` (`user_nick`)
) ENGINE=MyISAM AUTO_INCREMENT=45 DEFAULT CHARSET=utf8;

